import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import './bootstrap.min.css';
import { Provider } from 'react-redux';
import store from './store';
import { logout } from './redux/actions/user';
import { useDispatch } from 'react-redux';
import axios from '.axios';

const InitialRender = () => {
  const doAction = async () => {
    const userInfo = sessionStorage.getItem('userInfo')
      ? JSON.parse(sessionStorage.getItem('userInfo'))
      : null;

    const { email } = userInfo;
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };
    const { data } = await axios.post('http://localhost:5000/user/getuser', {
      email,
      config,
    });

    if (data.isLoggenIn && userInfo && document.visibilityState === 'visible') {
      console.log('userInfo');
      console.log(userInfo);
      console.log('debugger');

      const Y = window.confirm('already login index continue in this tab ?');

      if (Y === false) {
        userInfo && dispatch(logout(userInfo.email));
      } else {
      }
    }
  };

  const dispatch = useDispatch();

  document.addEventListener('visibilitychange', doAction);
  return <></>;
};

export default InitialRender;

ReactDOM.render(
  <Provider store={store}>
    <InitialRender />
    <App />
  </Provider>,
  document.getElementById('root')
);

reportWebVitals();
