import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import GoogleLogin from 'react-google-login';
import { login } from '../redux/actions/user';
import { useDispatch, useSelector } from 'react-redux';

const LoginScreen = ({ location, history }) => {
  const dispatch = useDispatch();

  const userLogin = useSelector((state) => state.userLogin);
  const { loading, error, userInfo } = userLogin;

  // const redirect = location.search ? location.search.split('=')[1] : '/';

  useEffect(() => {
    if (userInfo) {
      // history.push(redirect);
      console.log(userInfo);
    }
  }, [history, userInfo]);

  const handleSuccessLogin = (response) => {
    console.log(response.profileObj);
    // dispatch(login(email, password))
  };

  const handleFailLogin = (response) => {
    console.log(response);
  };

  return (
    <>
      <Container className="text-center my-5 py-5 shadow bg-light">
        <h1 className="text-success">Sign in Here</h1>

        <GoogleLogin
          className="mt-2 text-center"
          clientId="329337993518-7u332kcuq9agn3h1nec249froda0bqp3.apps.googleusercontent.com"
          buttonText="Log in with Google"
          onSuccess={handleSuccessLogin}
          onFailure={handleFailLogin}
          cookiePolicy={'single_host_origin'}
          theme="dark"
        />
      </Container>
    </>
  );
};

export default LoginScreen;
