import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import GoogleLogin from 'react-google-login';
import {
  login,
  isAlreadyLoggedIn,
  logout,
  gettingUserInfo,
} from '../redux/actions/user';
import { useDispatch, useSelector } from 'react-redux';

const LoginScreen = ({ location, history }) => {
  const dispatch = useDispatch();

  const userLogin = useSelector((state) => state.userLogin);
  const { loading, error, userInfo } = userLogin;

  const checkLogin = useSelector((state) => state.checkLogin);
  const { userLoggedIn } = checkLogin;

  const getUser = useSelector((state) => state.getUser);
  const { userData } = getUser;

  // const redirect = location.search ? location.search.split('=')[1] : '/';

  useEffect(() => {
    if (!userInfo) {
      history.push('/');
    } else {
      if (userData.isLoggedIn === true) {
        console.log(userData);
        console.log('userdata');
        const Y = window.confirm('already login continue in this tab ?');
        if (Y) {
          history.push('/dashboard');
        } else {
          dispatch(logout(userInfo.email));
          history.push('/');
        }
      }
    }
  }, [history, userInfo]);

  const handleSuccessLogin = (response) => {
    const { name, email, imageUrl } = response.profileObj;
    dispatch(gettingUserInfo(email));

    dispatch(login(name, email, imageUrl));
  };

  const handleFailLogin = (response) => {
    console.log(response);
  };

  return (
    <>
      <Container className="text-center my-5 py-5 shadow bg-light">
        <h1 className="text-success">Sign in Here</h1>

        <GoogleLogin
          className="mt-2 text-center"
          clientId="329337993518-7u332kcuq9agn3h1nec249froda0bqp3.apps.googleusercontent.com"
          buttonText="Log in with Google"
          onSuccess={handleSuccessLogin}
          onFailure={handleFailLogin}
          cookiePolicy={'single_host_origin'}
          theme="dark"
        />
      </Container>
    </>
  );
};

export default LoginScreen;
